import React from 'react';
import { Player } from '@remotion/player';
import MyVideoComposition from './MyVideoComposition';

const ResponsiveVideoPlayer: React.FC<{ videoUrl: string }> = ({ videoUrl }) => {
  return (
    <div className="video-container">
      <Player
        component={MyVideoComposition}  // Use the video composition
        durationInFrames={1500}  // Set the total number of frames
        compositionWidth={1080}
        compositionHeight={720}
        fps={30}
        inputProps={{ videoUrl }}  // Pass the video URL as a prop
        className="responsive-player"
      />
    </div>
  );
};

export default ResponsiveVideoPlayer;
