import { Player } from '@remotion/player';
import React, { useRef } from 'react';
import MyVideoComposition from './MyVideoComposition';
import PlayCircleFilledWhiteIcon from '@mui/icons-material/PlayCircleFilledWhite';
import PauseCircleFilledIcon from '@mui/icons-material/PauseCircleFilled'; // Importing pause icon

const ResponsiveVideoPlayer: React.FC<{ videoUrl: string; trimRange: [number, number] }> = ({ videoUrl, trimRange }) => {
  const playerRef = useRef<any>(null);  // Create a ref to access the Player

  const handlePlay = () => {
    if (playerRef.current) {
      playerRef.current.play();
    }
  };

  const handlePause = () => {
    if (playerRef.current) {
      playerRef.current.pause();
    }
  };

  return (
    <div className="video-container" style={{ margin: "20px" }}>
      <Player
        ref={playerRef}  // Assign the ref to the Player component
        component={MyVideoComposition}  // Use the video composition
        durationInFrames={1500}  // Set the total number of frames
        compositionWidth={1400}
        compositionHeight={500}
        fps={30}
        inputProps={{ videoUrl, trimRange }}  // Pass the video URL and trimRange as props
        className="responsive-player"
      />
      <div className="video-controls" style={{ marginLeft: "20px", display: "flex", alignItems: "center" }}>
        <button onClick={handlePlay} style={{ display: "flex", alignItems: "center" }}>
          <PlayCircleFilledWhiteIcon style={{ marginRight: "8px" }} /> {/* Add icon to the button */}
          Play
        </button>
        <button onClick={handlePause} style={{ marginLeft: "10px", display: "flex", alignItems: "center" }}>
          <PauseCircleFilledIcon style={{ marginRight: "8px" }} /> {/* Add icon to the button */}
          Pause
        </button>
      </div>
    </div>
  );
};

export default ResponsiveVideoPlayer;
