import { Player } from '@remotion/player';
import React, { useRef } from 'react';
import MyVideoComposition from './MyVideoComposition';

const ResponsiveVideoPlayer: React.FC<{ videoUrl: string; trimRange: [number, number] }> = ({ videoUrl, trimRange }) => {
  const playerRef = useRef<any>(null);  // Create a ref to access the Player

  const handlePlay = () => {
    if (playerRef.current) {
      playerRef.current.play();
    }
  };

  const handlePause = () => {
    if (playerRef.current) {
      playerRef.current.pause();
    }
  };

  return (
    <div className="video-container" style={{alignItems: "center", justifyContent: "center", justifyItems: "center"}}>
      <Player
        ref={playerRef}  // Assign the ref to the Player component
        component={MyVideoComposition}  // Use the video composition
        durationInFrames={1500}  // Set the total number of frames
        compositionWidth={1080}
        compositionHeight={500}
        fps={30}
        inputProps={{ videoUrl, trimRange }}  // Pass the video URL and trimRange as props
        className="responsive-player"
      />
      <div className="video-controls" style={{marginLeft: "50px"}}>
        <button onClick={handlePlay} >Play</button>
        <button onClick={handlePause} >Pause</button>
      </div>
    </div>
  );
};

export default ResponsiveVideoPlayer;
